import React, { useState } from "react";

export default function UploadPanel({ onVideoSelect }) {
  const [uploading, setUploading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [videoName, setVideoName] = useState("");

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    setVideoName(file.name);

    // ✅ Create a local preview immediately
    const localUrl = URL.createObjectURL(file);

    // ✅ Prepare and send video to backend
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:8000/process_video", {
        method: "POST",
        body: formData,
      });

      const result = await res.json();
      console.log("Backend response:", result);

      // ✅ Only mark as processing if upload was successful
      if (result.status === "ok") {
        setProcessing(true);
        // ✅ Start WebSocket after backend confirms upload
        onVideoSelect(localUrl);
      } else {
        alert("Backend error: " + (result.message || "Unknown error"));
      }
    } catch (err) {
      console.error("Upload error:", err);
      alert("Upload failed. Check backend connection.");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center border-2 border-dashed border-gray-600 rounded-lg p-6 text-center">
      <p className="mb-2 text-gray-300">
        {uploading
          ? "Uploading video..."
          : processing
          ? "Processing highlights..."
          : "Upload your football match video"}
      </p>

      <input
        type="file"
        accept="video/*"
        onChange={handleUpload}
        disabled={uploading} // ✅ only disable while uploading
        className="block w-full text-sm text-gray-400 file:mr-4 file:py-2 file:px-4 
                   file:rounded-md file:border-0 file:text-sm file:font-semibold
                   file:bg-green-500 file:text-white hover:file:bg-green-600"
      />

      {videoName && (
        <p className="text-gray-400 text-xs mt-2">
          Selected: <span className="text-white">{videoName}</span>
        </p>
      )}
    </div>
  );
}
