import React, { useState } from "react";
import Modal from "react-modal";
import { Scissors, Download, X } from "lucide-react";

Modal.setAppElement("#root"); // important for accessibility

export default function HighlightsPanel({ highlights }) {
  const [selectedHighlight, setSelectedHighlight] = useState(null);
  const [startTime, setStartTime] = useState(0);
  const [endTime, setEndTime] = useState(0);

  if (!highlights || highlights.length === 0) {
    return <p className="text-gray-400 italic">No goals detected yet…</p>;
  }

  // 🧠 Handler for opening modal
  const openEditor = (h) => {
    setSelectedHighlight(h);
    setStartTime(0);
    setEndTime(0);
  };

  const closeModal = () => {
    setSelectedHighlight(null);
  };

  const handleSaveTrim = () => {
    console.log(`Trim saved for ${selectedHighlight.title}: ${startTime}s to ${endTime}s`);
    alert(`Trim saved: ${startTime}s to ${endTime}s`);
    closeModal();
  };

  return (
    <div className="flex flex-col gap-3">
      {highlights.map((h) => (
        <div
          key={h.id}
          className="bg-gray-700 rounded-lg p-3 shadow-md hover:bg-gray-600 transition"
        >
          {/* Video */}
          <div className="relative mb-2">
            <video
              src={h.url}
              controls
              className="w-full rounded-md shadow-md"
            >
              Sorry, your browser doesn’t support embedded videos.
            </video>
          </div>

          {/* Title + Icons */}
          <div className="flex justify-between items-center">
            <h3 className="font-semibold text-lg text-white">{h.title}</h3>
            <div className="flex gap-3 text-xl">
              <button
                className="hover:text-pink-400"
                title="Trim/Edit this highlight"
                onClick={() => openEditor(h)}
              >
                <Scissors size={20} />
              </button>
              <button
                className="hover:text-blue-400"
                title="Download or export this highlight"
                onClick={() => window.open(h.url, "_blank")}
              >
                <Download size={20} />
              </button>
            </div>
          </div>
        </div>
      ))}

      {/* ✂️ Trim Modal */}
      <Modal
        isOpen={!!selectedHighlight}
        onRequestClose={closeModal}
        contentLabel="Trim Highlight"
        className="bg-gray-800 text-white p-6 rounded-xl w-[90%] max-w-lg mx-auto mt-20 shadow-2xl border border-gray-600"
        overlayClassName="fixed inset-0 bg-black bg-opacity-60 flex items-start justify-center z-50"
      >
        <div className="flex justify-between items-center mb-3">
          <h2 className="text-xl font-semibold text-yellow-400">
            ✂️ Trim Highlight - {selectedHighlight?.title}
          </h2>
          <button onClick={closeModal} className="text-gray-300 hover:text-white">
            <X size={22} />
          </button>
        </div>

        <video
          src={selectedHighlight?.url}
          controls
          className="w-full rounded-md mb-4"
        />

        <div className="space-y-3">
          <div className="flex justify-between items-center">
            <label className="text-gray-300">Start Time (s)</label>
            <input
              type="number"
              value={startTime}
              min="0"
              onChange={(e) => setStartTime(Number(e.target.value))}
              className="bg-gray-700 p-2 rounded-md w-24 text-center"
            />
          </div>

          <div className="flex justify-between items-center">
            <label className="text-gray-300">End Time (s)</label>
            <input
              type="number"
              value={endTime}
              min="0"
              onChange={(e) => setEndTime(Number(e.target.value))}
              className="bg-gray-700 p-2 rounded-md w-24 text-center"
            />
          </div>
        </div>

        <button
          className="mt-5 w-full bg-green-600 hover:bg-green-500 py-2 rounded-md font-semibold text-lg"
          onClick={handleSaveTrim}
        >
          💾 Save Trim
        </button>
      </Modal>
    </div>
  );
}
