import React from "react";

export default function HighlightsPanel({ highlights }) {
  if (highlights.length === 0) {
    return <p className="text-gray-400 italic">No goals detected yet...</p>;
  }

  return (
    <div className="grid grid-cols-2 gap-4">
      {highlights.map((clip) => (
        <div
          key={clip.id}
          className="bg-gray-700 p-3 rounded-lg shadow-md hover:shadow-xl transition-all"
        >
          <img
            src={clip.thumb}
            alt={clip.title}
            className="rounded-lg w-full mb-2"
          />
          <div className="flex justify-between items-center">
            <span className="font-medium">{clip.title}</span>
            <div className="flex gap-2 text-lg">
              <button className="hover:scale-110 transition-transform">✂️</button>
              <button className="hover:scale-110 transition-transform">⬇️</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
