import React, { useState, useEffect } from "react";
import UploadPanel from "./components/UploadPanel";
import HighlightsPanel from "./components/HighlightsPanel";

export default function App() {
  const [videoURL, setVideoURL] = useState(null);
  const [highlights, setHighlights] = useState([]);
  const [statusMessages, setStatusMessages] = useState([]); // NEW: progress messages

  useEffect(() => {
    const socket = new WebSocket("ws://127.0.0.1:8000/ws/goals");

    socket.onopen = () => {
      console.log("âœ… Connected to FastAPI WebSocket");
      setStatusMessages((prev) => [...prev, "Connected to backend"]);
    };

    socket.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        if (data.type === "status") {
          console.log("â„¹ï¸", data.message);
          setStatusMessages((prev) => [
            ...prev.slice(-10), // keep only last 10 messages
            data.message,
          ]);
        } else if (data.type === "goal") {
          console.log("ðŸŽ¥ New goal highlight:", data);
          setHighlights((prev) => [...prev, data]);
        }
      } catch (err) {
        console.error("Error parsing message:", err);
      }
    };

    socket.onclose = () => {
      console.log("âŒ WebSocket disconnected");
      setStatusMessages((prev) => [...prev, "Disconnected"]);
    };

    socket.onerror = (err) => {
      console.warn("âš ï¸ WebSocket error:", err);
      setStatusMessages((prev) => [...prev, "WebSocket error occurred"]);
    };

    return () => socket.close();
  }, []);

  return (
    <div className="h-screen flex flex-col bg-gray-900 text-white">
      <header className="text-center py-4 border-b border-gray-700">
        <h1 className="text-3xl font-bold text-green-400">
          Football Goal Highlights
        </h1>
      </header>

      <div className="flex flex-1 p-4 gap-4 overflow-hidden">
        {/* Left: Upload + Playback */}
        <div className="w-1/2 bg-gray-800 p-4 rounded-lg flex flex-col">
          <UploadPanel onVideoSelect={setVideoURL} />
          {videoURL && (
            <video
              src={videoURL}
              controls
              autoPlay
              className="mt-4 w-full rounded-lg shadow-lg"
            />
          )}
        </div>

        {/* Right: Highlights + Live status */}
        <div className="w-1/2 bg-gray-800 p-4 rounded-lg border border-gray-700 overflow-y-auto">
          <h2 className="text-2xl font-semibold text-yellow-400 mb-3">
            Goal Highlights
          </h2>

          <HighlightsPanel highlights={highlights} />

          {/* Live Progress Log */}
          <div className="mt-4 border-t border-gray-600 pt-3">
            <h3 className="text-lg font-semibold text-blue-400 mb-2">
              Live Pipeline Status
            </h3>
            <div className="text-sm text-gray-300 bg-gray-900 rounded-md p-3 h-40 overflow-y-auto font-mono">
              {statusMessages.length === 0 ? (
                <p className="italic text-gray-500">Waiting for updates...</p>
              ) : (
                statusMessages.map((msg, i) => (
                  <p key={i} className="leading-snug">
                    {msg}
                  </p>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
