import React, { useState, useEffect } from "react";
import UploadPanel from "./components/UploadPanel";
import HighlightsPanel from "./components/HighlightsPanel";

export default function App() {
  const [videoURL, setVideoURL] = useState(null);
  const [highlights, setHighlights] = useState([]);

  // Simulate receiving highlights from backend every few seconds
  useEffect(() => {
    if (videoURL) {
      const interval = setInterval(() => {
        const id = highlights.length + 1;
        const newHighlight = {
          id,
          title: `Goal #${id}`,
          thumb: `https://via.placeholder.com/300x180.png?text=Goal+${id}`,
        };
        setHighlights((prev) => [...prev, newHighlight]);
      }, 7000); // one new goal every 7 sec
      return () => clearInterval(interval);
    }
  }, [videoURL, highlights]);

  return (
    <div className="h-screen flex flex-col bg-gray-900 text-white">
      <header className="text-center py-4 border-b border-gray-700">
        <h1 className="text-3xl font-bold text-green-400">
          âš½ Football Goal Highlights
        </h1>
      </header>

      <div className="flex flex-1 p-4 gap-4 overflow-hidden">
        {/* Left: Video upload + playback */}
        <div className="w-1/2 bg-gray-800 p-4 rounded-lg flex flex-col">
          <UploadPanel onVideoSelect={setVideoURL} />
          {videoURL && (
            <video
              src={videoURL}
              controls
              autoPlay
              className="mt-4 w-full rounded-lg shadow-lg"
            />
          )}
        </div>

        {/* Right: Goal highlights */}
        <div className="w-1/2 bg-gray-800 p-4 rounded-lg border border-gray-700 overflow-y-auto">
          <h2 className="text-2xl font-semibold text-yellow-400 mb-3">
            Goal Highlights
          </h2>
          <HighlightsPanel highlights={highlights} />
        </div>
      </div>
    </div>
  );
}
