import React, { useState, useRef } from "react";
import Modal from "react-modal";
import { Scissors, Download, X } from "lucide-react";

Modal.setAppElement("#root");

export default function HighlightsPanel({ highlights }) {
  const [selectedHighlight, setSelectedHighlight] = useState(null);
  const [startTime, setStartTime] = useState(0);
  const [endTime, setEndTime] = useState(0);
  const videoRef = useRef(null);

  if (!highlights || highlights.length === 0) {
    return <p className="text-gray-400 italic">No goals detected yet…</p>;
  }

  const openEditor = (h) => {
    setSelectedHighlight(h);
    setStartTime(0);
    setEndTime(0);
  };

  const closeModal = () => {
    setSelectedHighlight(null);
  };

  const markStart = () => {
    if (videoRef.current) {
      const t = videoRef.current.currentTime.toFixed(2);
      setStartTime(parseFloat(t));
    }
  };

  const markEnd = () => {
    if (videoRef.current) {
      const t = videoRef.current.currentTime.toFixed(2);
      setEndTime(parseFloat(t));
    }
  };

  const handleSaveTrim = async () => {
    if (!selectedHighlight) return;
    if (endTime <= startTime) {
      alert("End time must be greater than start time!");
      return;
    }

    const payload = {
      id: selectedHighlight.id,
      start: startTime,
      end: endTime,
      url: selectedHighlight.url,
    };

    console.log("Sending trim data:", payload);

    const res = await fetch("http://127.0.0.1:8000/trim_video", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    const result = await res.json();
    alert(`Trim saved → ${result.output}`);
    closeModal();
  };

  return (
    <div className="flex flex-col gap-3">
      {highlights.map((h) => (
        <div
          key={h.id}
          className="bg-gray-700 rounded-lg p-3 shadow-md hover:bg-gray-600 transition"
        >
          <div className="relative mb-2">
            <video
              src={h.url}
              controls
              className="w-full rounded-md shadow-md"
            />
          </div>

          <div className="flex justify-between items-center">
            <h3 className="font-semibold text-lg text-white">{h.title}</h3>
            <div className="flex gap-3 text-xl">
              <button
                className="hover:text-pink-400"
                onClick={() => openEditor(h)}
                title="Trim/Edit this highlight"
              >
                <Scissors size={20} />
              </button>
              <button
                className="hover:text-blue-400"
                onClick={() => window.open(h.url, "_blank")}
                title="Download original clip"
              >
                <Download size={20} />
              </button>
            </div>
          </div>
        </div>
      ))}

      {/* TRIM MODAL */}
      <Modal
        isOpen={!!selectedHighlight}
        onRequestClose={closeModal}
        className="bg-gray-800 text-white p-6 rounded-xl w-[90%] max-w-lg mx-auto mt-20 shadow-2xl border border-gray-600"
        overlayClassName="fixed inset-0 bg-black bg-opacity-60 flex items-start justify-center z-50"
      >
        <div className="flex justify-between items-center mb-3">
          <h2 className="text-xl font-semibold text-yellow-400">
            ✂️ Trim Highlight - {selectedHighlight?.title}
          </h2>
          <button onClick={closeModal} className="text-gray-300 hover:text-white">
            <X size={22} />
          </button>
        </div>

        <video
          ref={videoRef}
          src={selectedHighlight?.url}
          controls
          className="w-full rounded-md mb-3"
        />

        <div className="flex justify-between items-center mb-4">
          <button
            onClick={markStart}
            className="bg-green-700 hover:bg-green-600 px-4 py-2 rounded-md"
          >
            ⏱ Mark Start
          </button>
          <button
            onClick={markEnd}
            className="bg-red-700 hover:bg-red-600 px-4 py-2 rounded-md"
          >
            ⏱ Mark End
          </button>
        </div>

        <div className="text-center text-gray-300 mb-4">
          <p>Start: {startTime}s</p>
          <p>End: {endTime}s</p>
        </div>

        <button
          onClick={handleSaveTrim}
          className="w-full bg-blue-600 hover:bg-blue-500 py-2 rounded-md font-semibold text-lg"
        >
          💾 Save Trim
        </button>
      </Modal>
    </div>
  );
}
